mapper.insert:
INSERT INTO
infrMember (
	ifmmSeq
	, ifmmName
	, ifmmId
	, ifmmPwd
	, ifmmGenderCd
	, ifmmDob
	, ifmmDelNy
	, ifmmGradeCd
	, ifmmRegDate
	, ifmmStatusCd
	, ifmmEmailConsentNy
	, ifmmSmsConsentNy
	, ifmmPushConsentNy
) VALUES (
	#{ifmmSeq}
	, #{ifmmName}
	, #{ifmmId}
	, #{ifmmPwd}
	, #{ifmmGenderCd}
	, #{ifmmDob}
	, 0
	, #{ifmmGradeCd}
	, #{ifmmRegDate}
	, 78
	, #{ifmmEmailConsentNy}
	, #{ifmmSmsConsentNy}
	, #{ifmmPushConsentNy}
);

INSERT INTO
infrMemberEmail (
	ifmmSeq
	, ifmeEmailFull
	, ifmeDefaultNy
) VALUES (
	#{ifmmSeq}
	, #{ifmeEmailFull}
	, 1
)

	INSERT INTO
	infrMemberAddress (
		ifmmSeq
		, ifmaZipCode
		, ifmaAddress1
		, ifmaAddress2
        , ifmaDefaultNy
	) VALUES (
		#{ifmmSeq}
		, #{ifmaZipCode}
		, #{ifmaAddress1}
		, #{ifmaAddress2}
        , 1
	);

	INSERT INTO
	infrMemberPhone (
		ifmmSeq
        , ifmpNumber
        , ifmpDeviceCd
        , ifmpDefaultNy
    ) VALUES (
		#{ifmmSeq}				
        , #{ifmpNumberMobile}		
        , 26
        , 1
    )

	INSERT INTO
	infrMemberPhone (
		ifmmSeq
        , ifmpNumber
        , ifmpDeviceCd
        , ifmpDefaultNy
    ) VALUES (
		#{ifmmSeq}
        , #{ifmpNumberHome}		
        , 25
        , 1
    )

mapper.update:
UPDATE infrMember
SET
	ifmmName = #{ifmmName}
	, ifmmId = #{ifmmId}
	, ifmmPwd = #{ifmmPwd}
	, ifmmGenderCd = #{ifmmGenderCd}
	, ifmmDob = #{ifmmDob}
	, ifmmDelNy = 0
	, ifmmGradeCd = #{ifmmGradeCd}
	, ifmmStatusCd = 78
	, ifmmEmailConsentNy = #{ifmmEmailConsentNy}
	, ifmmSmsConsentNy = #{ifmmSmsConsentNy}
	, ifmmPushConsentNy = #{ifmmPushConsentNy}
WHERE
	1 = 1
AND
	ifmmSeq = #{ifmmSeq}

UPDATE infrMemberAddress
SET
	ifmaZipCode = #{ifmaZipCode}
	, ifmaAddress1 = #{ifmaAddress1}
	, ifmaAddress2 = #{ifmaAddress2}
	, ifmaDefaultNy = 1
WHERE
	1 = 1
AND
	ifmmSeq = #{ifmmSeq}
